+            <label>Book ID</label>
+            <input
+              className="form-control mb-4"
+              type="text"
+              name="bookId"
+              placeholder="Book ID"
+              value={bookId}
+              onChange={handleBookId}
+            />
+            <label>Title</label>
+            <input
+              className="form-control mb-4"
+              type="text"
+              name="title"
+              placeholder="Book Title"
+              value={title}
+              onChange={handleTitle}
+            />
+            <label>Author</label>
+            <input
+              className="form-control mb-4"
+              type="text"
+              name="author"
+              placeholder="Books Author"
+              value={author}
+              onChange={handleAuthor}
+            />
+            <label>Year</label>
+            <input
+              className="form-control mb-4"
+              type="text"
+              name="year"
+              placeholder="Year"
+              value={year}
+              onChange={handleYear}
+            />
+            <label>Genre</label>
+            <input
+              className="form-control mb-4"
+              type="text"
+              name="genre"
+              placeholder="Books Genre"
+              value={genre}
+              onChange={handleGenre}
+            />
+            <label>Condition</label>
+            <input
+              className="form-control mb-4"
+              type="text"
+              name="condition"
+              placeholder="Books Condition"
+              value={condition}
+              onChange={handleCondition}
+            />
+            <label>Price</label>
+            <input
+              className="form-control mb-4"
+              type="number"
+              name="price"
+              placeholder="Books Price"
+              value={price}
+              onChange={handlePrice}
+              min={0}
+              max={100}
+            />
+            <label htmlFor="fileInput">Image file</label>
+            <input
+              className="form-control mb-4"
+              type="file"
+              name="imageUrl"
+              placeholder="Image URL"
+              value={imageUrl}
+              onChange={handleImageUrl}
+            />
+            <button className="btn btn-primary btn-round">Add Book</button>
+          </form>
+        </div>
+      </div>
+    </>
+  );
+}
+
+export default AddBooks;

