+              placeholder="Book Title"
+              value={title}
+              onChange={handleTitle}
+            /> 
+            <input
+              
+              type="text"
+              name="author"
+              placeholder="Books Author"
+              value={author}
+              onChange={handleAuthor}
+            />
+            <input
+              
+              type="text"
+              name="year"
+              placeholder="Year"
+              value={year}
+              onChange={handleYear}
+            />
+            <input
+              
+              type="text"
+              name="genre"
+              placeholder="Books Genre"
+              value={genre}
+              onChange={handleGenre}
+            />
+            <input
+              
+              type="text"
+              name="condition"
+              placeholder="Books Condition"
+              value={condition}
+              onChange={handleCondition}
+            />
+           
+            <input
+             
+              type="text"
+              pattern="\d*"
+              name="price"
+              placeholder="price"
+              value={price}
