+import { useState, useEffect } from "react";
+import axios from "axios";
+import { Link, useParams } from "react-router-dom";
+//import AddTask from "../components/AddTask";
+
+function BookDetailsPage() {
+
+    //const API_URL = "https://books.adaptable.app"
+    const API_URL = 'https://books.adaptable.app';
+
+    const { bookId } = useParams();
+
+    const [book, setBook] = useState(null);
+
+    const getBook = () => {
+        //axios.get(`${API_URL}/books/${bookId}?_embed=books`)
+        axios.get(`${API_URL}/books/${bookId}`)
+            .then((response) => {
+                console.log('response.data: ',response.data)
+                setBook(response.data);
+            })
+            .catch((error) => {
+                console.log("Error getting book details from the API...");
+                console.log(error)
+            });
+    };
+
+
+    useEffect(() => {
+        getBook();
+    }, []);
+
+
+    return (
+        <div className="BookDetailsPage">
+            {book === null
+                ? <p>Loading...</p>
+                : (<>
+                    <h1>{book.title}</h1>
+                    <p>{book.description}</p>
+                </>)
+            }
+
+            {/* <AddBooks bookId={bookId} callbackToRefreshProject={getBook} /> */}
+
+             {book &&
+                book.tasks.map((book) => (
+                    <li className="TaskCard card" key={book.id}>
+                        <h3>Title: {book.title}</h3>
+                       
+                        <p>Author: {book.author}</p>
+                        <p>Author: {book.author}</p>
+                    </li>
+                ))} 
+
+            <Link to="/books">
+                <button>Back to books</button>
+            </Link>
+
+            <Link to={`/books/edit/${bookId}`}>
+                <button>Edit book</button>
+            </Link>
+
+        </div>
+    );
+}
+
+export default BookDetailsPage;
